  .performance-chart {
    background: @card-bg;
    border-radius: 12px;
    padding: 1.5rem;
    margin-bottom: 1.5rem;
    border: 1px solid @border-color;
  }

  .chart-bar {
    height: 40px;
    background: linear-gradient(90deg, @primary-color, lighten(@primary-color, 15%));
    border-radius: 20px;
    margin-bottom: 1rem;
    position: relative;
    overflow: hidden;
  }

  .bar-label {
    position: absolute;
    left: 15px;
    top: 50%;
    transform: translateY(-50%);
    color: white;
    font-weight: 500;
  }

  .bar-value {
    position: absolute;
    right: 15px;
    top: 50%;
    transform: translateY(-50%);
    color: white;
    font-weight: bold;
  }

  .chart-note {
    text-align: center;
    font-size: 0.9rem;
    color: lighten(@text-color, 30%);
    margin-top: 1rem;
  }

  .comparison-table {
    overflow-x: auto;

    table {
      width: 100%;
      border-collapse: collapse;
      border-radius: 12px;
      overflow: hidden;
    }

    th,
    td {
      padding: 1rem;
      text-align: center;
      border: 1px solid @border-color;
    }

    th {
      background: lighten(@primary-color, 40%);
      color: darken(@primary-color, 15%);
      font-weight: 600;
    }

    tr:nth-child(even) {
      background: lighten(@primary-color, 46%);
    }
  }

  .footer {
    text-align: center;
    color: lighten(@text-color, 30%);
    font-size: 0.9rem;
    padding-top: 2rem;
    margin-top: 1rem;
    border-top: 1px solid @border-color;

    a {
      color: @primary-color;
      text-decoration: none;
      font-weight: 500;

      &:hover {
        text-decoration: underline;
      }
    }
  }

  // 图标样式
  .icon {
    font-size: 1.6rem;

    &-engine:before {
      content: "⚙️";
    }

    &-info:before {
      content: "ℹ️";
    }

    &-google:before {
      content: "🔍";
    }

    &-browser:before {
      content: "🌐";
    }

    &-performance:before {
      content: "🚀";
    }

    &-gears:before {
      content: "🔧";
    }

    &-check:before {
      content: "✅";
    }

    &-warning:before {
      content: "⚠️";
    }

    &-applications:before {
      content: "📱";
    }

    &-chart:before {
      content: "📊";
    }

    &-speed:before {
      content: "⏩";
    }

    &-memory:before {
      content: "💾";
    }

    &-optimization:before {
      content: "🎯";
    }

    &-cross-platform:before {
      content: "🔄";
    }

    &-memory-usage:before {
      content: "📈";
    }

    &-warmup:before {
      content: "⏳";
    }

    &-complexity:before {
      content: "🧩";
    }

    &-deoptimization:before {
      content: "↩️";
    }

    &-check-circle:before {
      content: "✔️";
    }

    &-times-circle:before {
      content: "✖️";
    }
  }
}
</style>
