function multiply(a, b = 1) {
  return a * b;
}

function sum(...numbers) {
  return numbers.reduce((a, b) => a + b);
}

function factorial(n, total = 1) {
  if (n === 0) return total;
  return factorial(n - 1, n * total);
}

